{
  "openapi": "3.0.0",
  "info": {
    "title": "AgentCore Tool",
    "version": "1.0.0",
    "description": "Action group schema for invoking AgentCore via Lambda."
  },
  "paths": {
    "/invokeAgentCore": {
      "post": {
        "description": "Calls AgentCore and returns the response text plus optional metadata.",
        "operationId": "invokeAgentCore",
        "requestBody": {
          "required": true,
          "content": {
            "application/json": {
              "schema": {
                "type": "object",
                "properties": {
                  "prompt": {
                    "type": "string",
                    "description": "User prompt or question to send to AgentCore."
                  },
                  "timeout_s": {
                    "type": "integer",
                    "minimum": 1,
                    "maximum": 60,
                    "description": "Optional timeout override (seconds)."
                  },
                  "metadata": {
                    "type": "object",
                    "additionalProperties": { "type": "string" },
                    "description": "Optional metadata passed through to AgentCore."
                  }
                },
                "required": ["prompt"]
              }
            }
          }
        },
        "responses": {
          "200": {
            "description": "Successful response",
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "properties": {
                    "answer": { "type": "string", "description": "AgentCore response text." },
                    "raw": { "type": "object", "description": "Optional raw payload from AgentCore." }
                  },
                  "required": ["answer"]
                }
              }
            }
          }
        }
      }
    }
  }
}
